INSERT INTO `contents` (`id`, `title`, `slug`, `excerpt`, `content`, `desc`, `premium`, `keywords`, `created_date`, `view`, `state`, `comment_status`, `category_id`, `video`, `translate`, `image`) VALUES
(1, 'Dịch vụ tin cậy vì người tiêu dùng 2012', 'tienganh123-tham-gia-cuoc-binh-chon-top-100-san-pham-dich-vu-tin-cay-vi-nguoi-tieu-dung-2012', NULL, '<p>&nbsp;</p>\r\n<p style="color: #333333; font-family: Arial; font-size: 13px; line-height: 23px;"><img src="http://www.tienganh123.com/file/news/tintuc/bai34-top100/tin_top100.png" alt="" width="338" height="263" /></p>\r\n<p style="color: #333333; font-family: Arial; font-size: 13px; line-height: 23px;" align="justify">Nhằm ủng hộ chủ trương của ch&iacute;nh phủ trong cuộc vận động "Người Việt Nam ưu ti&ecirc;n d&ugrave;ng h&agrave;ng Việt Nam", TiếngAnh123.Com đ&atilde; tham gia cuộc b&igrave;nh chọn "Top 100 sản phẩm - dịch vụ v&igrave; người ti&ecirc;u d&ugrave;ng 2012". Cuộc b&igrave;nh chọn được bảo trợ bởi Ủy Ban TW Mặt Trận Tổ Quốc Việt Nam, Cơ quan thường trực Ban chỉ đạo TW cuộc vận động "Người Việt Nam ưu ti&ecirc;n d&ugrave;ng h&agrave;ng Việt Nam", Hội ti&ecirc;u chuẩn v&agrave; bảo vệ người ti&ecirc;u d&ugrave;ng Việt Nam v&agrave; chương tr&igrave;nh sẽ được truyền h&igrave;nh trực tiếp tr&ecirc;n k&ecirc;nh H1 - Đ&agrave;i truyền h&igrave;nh H&agrave; Nội v&agrave;o ng&agrave;y 11/11/2012.</p>\r\n<p style="color: #333333; font-family: Arial; font-size: 13px; line-height: 23px;" align="justify">Mặc d&ugrave; tham gia muộn - nhưng TiengAnh123.Com mong c&aacute;c th&agrave;nh vi&ecirc;n h&atilde;y ủng hộ v&agrave; b&igrave;nh chọn cho TiếngAnh123. Đ&acirc;y l&agrave; nguồn động vi&ecirc;n v&ocirc; c&ugrave;ng to lớn với cộng đồng của ch&uacute;ng ta.</p>\r\n<p style="color: #333333; font-family: Arial; font-size: 13px; line-height: 23px;" align="justify">Để b&igrave;nh chọn - c&aacute;c bạn h&atilde;y click v&agrave;o link sau đ&acirc;y:&nbsp;<a style="font-size: 10pt; color: #336699; text-decoration: none;" href="http://sanphamtincay.vn/Cong-ty-TNHH-Hay-Truc-Tuyen-BeOnline-Co-Ltd-133.html" target="_blank">Tham Gia B&igrave;nh Chọn</a>.</p>\r\n<p style="color: #333333; font-family: Arial; font-size: 13px; line-height: 23px;" align="justify"><a class="tip" title="đấm v&agrave;o muồm" href="#">Sau đ&oacute; c&aacute;c bạn h&atilde;y chọn c&acirc;u trả lời cho c&acirc;u hỏi xem TiếngAnh123 c&oacute; đảm bảo chất lượng với người ti&ecirc;u d&ugrave;ng hay kh&ocirc;ng? Rồi bấm n&uacute;t "B&igrave;nh chọn". Sau đ&oacute; bạn h&atilde;y điền họ t&ecirc;n, địa chỉ, số điện thoại, email, rồi bấm n&uacute;t "gửi b&igrave;nh chọn".</a></p>\r\n<p style="color: #333333; font-family: Arial; font-size: 13px; line-height: 23px;" align="justify">H&atilde;y b&igrave;nh chọn cho TiếngAnh123 c&aacute;c bạn nh&eacute;:</p>', 'Nhằm ủng hộ chủ trương của chính phủ trong cuộc vận động "Người Việt Nam ưu tiên dùng hàng Việt Nam", TiếngAnh123.Com đã tham gia cuộc bình chọn "Top 100 sản phẩm - dịch vụ vì người tiêu dùng 2012". Cuộc bình chọn được bảo trợ bởi Ủy Ban TW Mặt Trận Tổ Quốc Việt Nam', 0, NULL, '2012-10-24 04:39:16', 7, 1, NULL, 2, '', '', ''),
(2, 'Khoảnh khắc cùng người phụ nữ tôi yêu', 'ket-qua-thi-anh-khoanh-khac-cung-nguoi-phu-nu-toi-yeu', NULL, '<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<div>\r\n<h2 style="padding: 0px !important; margin: 0px !important; font-family: Arial; line-height: 23px; color: #0000ff;" align="center">KẾT QUẢ CUỘC THI ẢNH</h2>\r\n<p style="padding: 0px; margin: 0px; font-family: Arial; font-size: 13px; line-height: 23px; color: #0000ff;" align="center">&nbsp;</p>\r\n<h1 style="padding: 0px !important; margin: 0px !important; font-family: Arial; line-height: 23px; color: #0000ff;" align="center">&ldquo;KHOẢNH KHẮC C&Ugrave;NG NGƯỜI PHỤ NỮ T&Ocirc;I Y&Ecirc;U&rdquo;</h1>\r\n<p style="padding: 0px; margin: 0px; font-family: Arial; font-size: 13px; line-height: 23px; color: #0000ff;" align="center">&nbsp;</p>\r\n<p style="padding: 0px; margin: 0px; color: #333333; font-family: Arial; font-size: 13px; line-height: 23px;" align="center"><img src="http://www.tienganh123.com/file/news/tintuc/bai33-thi-anh-20-10/vnm_2012_492978.jpg" alt="" width="480" height="290" /></p>\r\n<p style="padding: 0px; margin: 0px; color: #333333; font-family: Arial; font-size: 13px; line-height: 23px;" align="center">T&aacute;c phẩm đoạt giải nhất do độc giả b&igrave;nh chọn</p>\r\n<p style="padding: 0px; margin: 0px; color: #333333; font-family: Arial; font-size: 13px; line-height: 23px;" align="center">&nbsp;</p>\r\n<p style="padding: 0px; margin: 0px; color: #333333; font-family: Arial; font-size: 13px; line-height: 23px;">Cuộc thi ảnh &ldquo;Khoảnh khắc c&ugrave;ng người phụ nữ t&ocirc;i y&ecirc;u&rdquo; đ&atilde; ch&iacute;nh thức kết th&uacute;c ng&agrave;y 21/10/2012 với thật nhiều t&igrave;nh cảm của những người quan t&acirc;m. Trải qua tr&ograve;n 1 th&aacute;ng, BTC đ&atilde; nhận được gần 1500 b&agrave;i dự thi với h&agrave;ng ng&agrave;n lượt xem v&agrave; b&igrave;nh chọn mỗi ng&agrave;y. Mỗi bức ảnh l&agrave; một c&acirc;u chuyện, l&agrave; t&igrave;nh cảm ch&acirc;n th&agrave;nh v&agrave; s&acirc;u sắc m&agrave; người dự thi muốn d&agrave;nh tặng cho những người phụ nữ của m&igrave;nh.</p>\r\n<p style="padding: 0px; margin: 0px; color: #333333; font-family: Arial; font-size: 13px; line-height: 23px;">&nbsp;</p>\r\n<p style="padding: 0px; margin: 0px; color: #333333; font-family: Arial; font-size: 13px; line-height: 23px;">C&aacute;c bạn v&agrave;o phần &ldquo;C&aacute;c b&agrave;i thi đoạt giải&rdquo; để xem kết quả nh&eacute;:</p>\r\n<p style="padding: 0px; margin: 0px; color: #333333; font-family: Arial; font-size: 13px; line-height: 23px;"><a style="font-size: 10pt; color: #336699; text-decoration: none;" href="http://www.facebook.com/pages/Tieng-Anh-123/174446175978225?sk=app_198060796994368">http://www.facebook.com/pages/Tieng-Anh-123/174446175978225?sk=app_198060796994368</a></p>\r\n<p style="padding: 0px; margin: 0px; color: #333333; font-family: Arial; font-size: 13px; line-height: 23px;">&nbsp;</p>\r\n<table style="color: #333333; font-family: Arial; font-size: 13px; line-height: 23px; width: 610px;" border="1" cellspacing="0" cellpadding="0" align="center">\r\n<tbody>\r\n<tr>\r\n<td style="font-size: 10pt;" width="51">\r\n<p style="padding: 0px; margin: 0px;" align="center"><strong>Giải</strong></p>\r\n</td>\r\n<td style="font-size: 10pt;" width="145">\r\n<p style="padding: 0px; margin: 0px;" align="center"><strong>T&ecirc;n người đoạt giải</strong></p>\r\n</td>\r\n<td style="font-size: 10pt;" width="264">\r\n<p style="padding: 0px; margin: 0px;" align="center"><strong>Địa chỉ</strong></p>\r\n</td>\r\n<td style="font-size: 10pt;" width="70">\r\n<p style="padding: 0px; margin: 0px;" align="center"><strong>M&atilde; số ảnh</strong></p>\r\n</td>\r\n<td style="font-size: 10pt;" width="68">\r\n<p style="padding: 0px; margin: 0px;" align="center">Số like</p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td style="font-size: 10pt;" width="51">\r\n<p style="padding: 0px; margin: 0px;">Nhất</p>\r\n</td>\r\n<td style="font-size: 10pt;" valign="middle" width="145">\r\n<p style=