--
-- Table structure for table `danhngon`
--

CREATE TABLE IF NOT EXISTS `danhngon` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `state` int(1) NOT NULL,
  `content` text NOT NULL,
  `created_date` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP,
  `author` varchar(255) DEFAULT NULL,
  `audio` varchar(255) DEFAULT NULL,
  `title` varchar(255) NOT NULL,
  `dictionary` text NOT NULL,
  PRIMARY KEY (`id`)
) ENGINE=InnoDB  DEFAULT CHARSET=utf8 AUTO_INCREMENT=18 ;

--
-- Dumping data for table `danhngon`
--

INSERT INTO `danhngon` (`id`, `state`, `content`, `created_date`, `author`, `audio`, `title`, `dictionary`) VALUES
(11, 1, '', '2012-11-05 08:36:18', 'Czech Proverb', NULL, 'Do not protect yourself by a fence, but rather by your friends', 'Đừng bảo vệ bản thân bằng một hàng rào, tốt hơn là bằng những người bạn của mình.'),
(12, 1, '', '2012-11-05 08:37:40', 'Dale Carnegie', NULL, 'You can make more friends in two months by becoming interested in other people than you can in two years by trying to get other people interested in you.', 'Bạn có thể có được nhiều bạn bè trong 2 tháng bằng cách trở lên quan tâm đến người khác hơn là trong 2 năm bằng cách cố gắng làm những người khác quan tâm đến bạn.'),
(13, 1, '', '2012-11-05 08:38:25', 'George Washington', NULL, 'Be courteous to all, but intimate with few, and let those few be well tried before you give them your confidence. True friendship is a plant of slow growth, and must undergo and withstand the shocks of adversity before it is entitled to the appellation.', 'Hãy lịch sự với tất cả mọi người, nhưng chỉ mật thiết với ít người, và hãy để số ít đó bị thử thách thật nhiều trước khi bạn cho họ biết những điều bí mật của mình. Tình bạn thực sự như là một cái cây lớn dần dần, và phải trải qua và chịu đựng được những cú sốc của nghịch cảnh trước khi nó được xứng đáng gọi như vậy.'),
(14, 1, '', '2012-11-05 08:38:51', 'Marlene Dietrick ', NULL, 'Friendship: a building contract you sign with laughter and break with tears.', 'Tình bạn: một bản hợp đồng bạn kí kết bằng tiếng cười và phá vỡ bằng nước mắt.'),
(15, 1, '', '2012-11-05 08:39:21', 'Frank Hall Crane ', NULL, '"What is a friend? I will tell you...it is someone with whom you dare to be yourself."', 'Thế nào là một người bạn? Tôi sẽ nói anh nghe... chính là người nào đó, người mà bên họ anh dám là chính mình.'),
(16, 1, '', '2012-11-05 08:40:09', 'Aristotle ', NULL, 'Without friends no one would choose to live, though he had all other goods.', 'Không có bạn bè thì chẳng ai sẽ chọn để sống, mặc dù anh ta có tất cả những thứ khác đi chăng nữa.'),
(17, 1, '', '2012-11-05 08:40:48', 'Ali ibn-Abi-Talib', NULL, 'He who has a thousand friends has not a friend to spare, And he who has one enemy will meet him everywhere', 'Có hàng trăm bạn hữu cũng chẳng thừa một ai, có một kẻ thù thì sẽ gặp ở khắp chốn.');

